								<li data-layout="2_3,1_3">
									<span class="column-block-wrap" style="width: 66.6667%;"><span></span></span>
									<span class="column-block-wrap" style="width: 33.3333%;"><span></span></span>
								</li>
								 
								<li data-layout="1_3,2_3">
									<span class="column-block-wrap" style="width: 33.3333%;"><span></span></span>
									<span class="column-block-wrap" style="width: 66.6667%;"><span></span></span>
								</li>
								
								<li data-layout="1_4,3_4">
									<span class="column-block-wrap" style="width: 25%;"><span></span></span>
									<span class="column-block-wrap" style="width: 75%;"><span></span></span>
								</li>
								
								<li data-layout="3_4,1_4">
									<span class="column-block-wrap" style="width: 75%;"><span></span></span>
									<span class="column-block-wrap" style="width: 25%;"><span></span></span>
								</li>
								
								<li data-layout="1_2,1_4,1_4">
